import {Component} from "react";
import Pokedex from "../pokedex.component/pokedex.component";

class Pokegame extends Component{
    static defaultProps = {
        pokemon : [
            { id: 4, name: 'Charmander', type: 'fire', base_experience: 62 },
            { id: 7, name: 'Squirtle', type: 'water', base_experience: 63 },
            { id: 11, name: 'Metapod', type: 'bug', base_experience: 72 },
            { id: 12, name: 'Butterfree', type: 'flying', base_experience: 178 },
            { id: 25, name: 'Pikachu', type: 'electric', base_experience: 112 },
            { id: 39, name: 'Jigglypuff', type: 'normal', base_experience: 95 },
            { id: 94, name: 'Gengar', type: 'poison', base_experience: 225 },
            { id: 133, name: 'Eevee', type: 'normal', base_experience: 65 }
        ]
    };

    render() {
        let hand1 = [];
        let hand2 = [...this.props.pokemon];
        while (hand1.length<hand2.length){
            let randIdx = Math.floor(Math.random()*hand2.length);
            let randPokemon = hand2.slice(randIdx, 1)[0];
            hand1.push(randPokemon);
        }
        console.log(hand1);
        console.log(hand2);

        let exp1 = hand1.reduce((exp,pokemon) => exp + pokemon.base_experience, 0);
        let exp2 = hand2.reduce((exp,pokemon) => exp + pokemon.base_experience, 0);
        return(
            <div>
                <Pokedex pokemon={hand1} exp={exp1} isWinner={exp1>exp2}></Pokedex>
                <Pokedex pokemon={hand2} exp={exp2} isWinner={exp2>exp1}></Pokedex>
            </div>
        );
    }
}
export default Pokegame;
